      .then(
        () => {
          if (!session.isOpen) return;

          this.resolve.delete(session);
          this.reject.delete(session);
        },
        err => {
          session.destroy(err);
        },
      );
  }

  exec(session) {
    this.fetch(session);
  }

  toArgumentsList() {
    return [this.target, this.resolve, this.reject];
  }
