{
  "source": "packages",
  "destination": "./docs",
  "excludes": [
    "/node_modules/",
    "/lib/",
    "/test/"
  ],
  "package": "./.esdoc.json",
  "plugins": [{
    "name": "esdoc-standard-plugin",
    "option": {
      "lint": {
        "enable": true
      },
      "accessor": {
        "access": ["public", "protected"],
        "autoPrivate": true
      },
      "brand": {
        "title": "Remotelib",
        "description": "Run code remotely",
        "repository": "https://github.com/remotelib/remote-lib",
        "site": "http://wwww.remotelib.com"
      },
      "manual": {
        "index": "./README.md",
        "files": [
          "./CHANGELOG.md"
        ]
      }
    }
  }, {
    "name": "esdoc-importpath-plugin",
    "option": {
      "replaces": [{
        "from": "/src/",
        "to": "/lib/"
      }, {
        "from": "^./packages/",
        "to": ""
      }, {
        "from": "reference-context/lib/reference-context.js$",
        "to": "reference-context"
      }, {
        "from": "remote-instance/lib/parser.js$",
        "to": "remote-instance"
      }]
    }
  }]
}
